sp_widget.client_script:
function($scope, $location, snAttachmentHandler, cabrillo, spUtil) {
	/* widget controller */
	var c = this;	

	addStyle('@media (min-width: 1200px){ .container { width: 65% !important; }}')
	function addStyle(styleText) {
		var style = document.createElement('style');
		style.innerHTML = styleText;
		document.head.appendChild(style);
	}

	c.data.table_label = [
		{
			key: 'number',
			label: 'Request ID'
		},
		{
			key: 'principal_investigator',
			label: 'Principal Investigator'
		},
		{
			key: 'can_number',
			label: 'CAN Number'
		}, 
		{
			key: 'type',
			label: 'Resource Type'
		},
		{
			key: 'submitted_by',
			label: 'Submitted By'
		}, 
		{
			key: 'stage',
			label: 'Status'
		}
	];
	$scope.pagination = [5, 10, 15, 20, 25];
	$scope.sortKey = null;
	$scope.reverse = false;	
	$scope.sort = function(keyname){
    /*console.log($scope.sortKey);*/
		/*console.log();*/
		$scope.sortKey = keyname;   //set the sortKey to the param passed
		$scope.reverse = !$scope.reverse; //if true make it false and vice versa
	}	
	spUtil.recordWatch($scope, "x_naci_tissue_requ_tissue_resource_request", "", function(name, data) {  
			spUtil.update($scope);
	});

	c.viewSupervisor = function(sys_id){
		top.window.location = "/ncisp?id=display_tissue_request_cat_item&sys_id=" + sys_id;	
	}
}

